<?php

namespace q\module;

use q\core;
use q\view;
use q\core\helper as h;
use q\asset;

// load it up ##
\q\module\prism::__run();

class prism extends \Q {
    
    static $args = array();

    public static function __run()
    {

		// add extra options in module select API ##
		\add_filter( 'acf/load_field/name=q_option_module', [ get_class(), 'filter_acf_module' ], 10, 1 );

		// make running dependent on module selection in Q settings ##
		if ( 
			! isset( core\option::get('module')->prism )
			|| true !== core\option::get('module')->prism 
		){

			// h::log( 'd:>prism is not enabled.' );

			return false;

		}

		// add css to theme ##
        \add_action( 'wp_head', function(){
			asset\css::ob_get([
				'view'      => get_class(), 
				'method'    => 'css',
			]);
		}, 3 );
	
		// add JS to footer/script ##
        \add_action( 'wp_footer', function(){
			asset\javascript::ob_get([
				'view'      => get_class(), 
				'method'    => 'javascript',
			]);
		}, 3 );

    }

	

	/**
     * Add new libraries to Q Settings via API
     * 
     * @since 2.3.0
     */
    public static function filter_acf_module( $field )
    {

		// pop on a new choice ##
		$field['choices']['prism'] = 'Prism Syntax Highlighter';

		// make it selected ##
		$field['default_value'][0] = 'prism';

		// kick back ##
		return $field;

	}



	
	/**
    * CSS for Prism Syntax Highlighter
	*
	* @link 	https://prismjs.com/download.html#themes=prism-tomorrow&languages=markup+css+clike+javascript+js-templates+markup-templating+php
    * @since    2.0.0
    * @return   String
    */
	public static function css(){

?>
<style>
	/* PrismJS 1.21.0
	https://prismjs.com/download.html#themes=prism-tomorrow&languages=markup+css+clike+javascript+js-templates+markup-templating+php */
	/**
	* prism.js tomorrow night eighties for JavaScript, CoffeeScript, CSS and HTML
	* Based on https://github.com/chriskempson/tomorrow-theme
	* @author Rose Pritchard
	*/
	code[class*="language-"],
	pre[class*="language-"] {
		color: #ccc;
		background: none;
		font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
		font-size: 1em;
		text-align: left;
		white-space: pre;
		word-spacing: normal;
		word-break: normal;
		word-wrap: normal;
		line-height: 1.5;

		-moz-tab-size: 4;
		-o-tab-size: 4;
		tab-size: 4;

		-webkit-hyphens: none;
		-moz-hyphens: none;
		-ms-hyphens: none;
		hyphens: none;

	}

	/* Code blocks */
	pre[class*="language-"] {
		padding: 1em;
		margin: .5em 0;
		overflow: auto;
	}

	:not(pre) > code[class*="language-"],
	pre[class*="language-"] {
		background: #2d2d2d;
	}

	/* Inline code */
	:not(pre) > code[class*="language-"] {
		padding: .1em;
		border-radius: .3em;
		white-space: normal;
	}

	.token.comment,
	.token.block-comment,
	.token.prolog,
	.token.doctype,
	.token.cdata {
		color: #999;
	}

	.token.punctuation {
		color: #ccc;
	}

	.token.tag,
	.token.attr-name,
	.token.namespace,
	.token.deleted {
		color: #e2777a;
	}

	.token.function-name {
		color: #6196cc;
	}

	.token.boolean,
	.token.number,
	.token.function {
		color: #f08d49;
	}

	.token.property,
	.token.class-name,
	.token.constant,
	.token.symbol {
		color: #f8c555;
	}

	.token.selector,
	.token.important,
	.token.atrule,
	.token.keyword,
	.token.builtin {
		color: #cc99cd;
	}

	.token.string,
	.token.char,
	.token.attr-value,
	.token.regex,
	.token.variable {
		color: #7ec699;
	}

	.token.operator,
	.token.entity,
	.token.url {
		color: #67cdcc;
	}

	.token.important,
	.token.bold {
		font-weight: bold;
	}
	.token.italic {
		font-style: italic;
	}

	.token.entity {
		cursor: help;
	}

	.token.inserted {
		color: green;
	}
</style>
<?php
		
	}
	
    
    
